'use strict';

const adminRoutes = require('./routes');
const adminControllers = require('./controllers');

class LacoAdmin {
    constructor(laco) {
        this.laco = laco;
        let admin = new this.laco.SubApp('admin');

        admin.router.register(adminRoutes);
        admin.controller.register(adminControllers);
        this.laco.subAppManager.register('/admin', admin);
    }
}

module.exports = LacoAdmin;