'use strict';

const mongoose = require('mongoose');
const autoIncrement = require('mongoose-auto-increment');
const helper = lacoLib('libs/helper');
const db = require('./db');

db.schemas.conversation = new mongoose.Schema({
    creatorid: {
        type: Number,
        required: [true, 'CreatorId is required'],
        unique: true
    },
    targetid: {
        type: Number,
        required: [true, 'TargetId is required'],
    },
    identity: {
        type: String,
        required: [true, 'Identity is required'],
        unique: true
    },
    created_date: {
        type: Date,
        default: Date.now
    },
    updated_date: {
        type: Date,
        default: Date.now
    },
});

db.schemas.conversation.index({
    creatorid: 1,
    targetid: 1,
    identity: 1
}, {
    weights: {
        creatorid: 10,
        targetid: 10,
        identity: 10,
    },
    name: 'conversationIndex'
});

db.schemas.conversation.plugin(autoIncrement.plugin, {
    model: 'conversations',
    field: 'intId',
    startAt: 1,
    incrementBy: 1
});

db.models.conversation = mongoose.model('conversations', db.schemas.conversation);