"use strict";

const AdminModule = lacoLib('./libs/admin_module');

class Test extends AdminModule {
    constructor() {
        super(__dirname, 'Test');
        this.registerViews([
            'main'
        ]);

        this.registerMenus([
            {
                name: 'test_main',
                anchor: 'Test',
                link: this.link(),
                icon: '<i class="zmdi zmdi-badge-check"></i>'
            }
        ]);
    }

    main(req, res) {
        this.view('main', {}, req, res);
    }
}
module.exports = Test;
