"use strict";
const _ = require('underscore');
const logger = require('../logger');
let adminObjects = {};
let adminThemeLayouts = {
    default: lacoLib('./data/views/admin/default/layout_default.marko')
};

let init = (laco) => {
    const Dashboard = lacoLib('./admin/dashboard');
    const Taxonomy = lacoLib('./admin/taxonomy');
    const Media = lacoLib('./admin/media');
    const Test = lacoLib('./admin/test');
    
    adminObjects.dashboard = new Dashboard(laco);
    adminObjects.taxonomy = new Taxonomy(laco);
    adminObjects.media = new Media(laco);
    adminObjects.test = new Test(laco);
}
let registerAdminObject = adminObjects => {
    if(!_.isArray(adminObjects)) adminObjects = [adminObjects];
}

exports.init = init;
exports.adminObjects = adminObjects;
exports.adminThemeLayouts = adminThemeLayouts;