"use strict";
const commander = require('commander');

const init = () => {
    commander.version('1.1.0')
        .option('-i, --install', 'Install current Laco app')
        .option('-u, --update', 'Update Laco core package')
        .option('-r, --run', 'Run current Laco app')
        .parse(process.argv);

    if (commander.install) console.log('  - install');
}

const isCommand = command => commander[command] || false;

exports.init = init;
exports.isCommand = isCommand;