'use strict';

const EventEmitter = require('../EventEmitter');
const helper = lacoLib('libs/helper');
const path = require('path');

class BaseExtension extends EventEmitter {
    constructor(laco) {
        super();
        this.laco = laco;
        this.caller = helper.caller();
        this.ext = {
            dir: this.caller.dir,
            file: path.join(APP_ROOT, this.caller.file)
        };
    }

    init() {
        this.laco.emit(`ext.${this.name}.init.success`);
    }
}

module.exports = BaseExtension;