"use strict";

var path = require('path');
var helper = require(path.join(LIBS_DIR, 'helper'));

module.exports = function(name, data) {
    var fieldId = name.replace('[', '_');
    fieldId = fieldId.replace(']', '_');
    var f = '\
    <div class="input-field nbfile-field col s12 ' + data.cols + '">\
        <input id="' + fieldId + '" type="hidden" name="' + name + '" value="' + data.value + '" />\
        <div class="clearfix">\
            <a class="btn waves-effect waves-light select-media-btn nb-add-image-btn" data-field="' + fieldId + '" data-fieldtype="file">Add ' + data.label + '</a>\
            <a class="btn red waves-effect waves-light select-media-btn nb-remnove-file-btn" data-field="' + fieldId + '" data-fieldtype="file">Remove ' + data.label + '</a>\
        </div>\
        <div id="thumb-' + fieldId + '" class="nb-file-field-placeholder">';
            if(data.value != '') {
                f += '<i class="zmdi zmdi-attachment-alt"></i><a target="_blank" href="' + data.value + '">' + helper.nbGetFileName(data.value) + '</a>';
            }
        f += '\
        </div>\
    </div>';
    return f;
};