"use strict";

module.exports = function(name, data) {
    var fieldId = name.replace('[', '_');
    fieldId = fieldId.replace(']', '_');
    return '\
    <div class="input-field html-field col s12 m' + data.cols + '">\
        <label for="' + fieldId + '" class="active">' + data.label + '</label>\
        <textarea id="' + fieldId + '" name="' + name + '" class="materialize-textarea nb-html-editor">' + data.value + '</textarea>\
    </div>\
    ';
};