"use strict";

module.exports = function(name, data) {
    var fieldId = name.replace('[', '_');
    fieldId = fieldId.replace(']', '_');
    return '\
    <div class="input-field col s12 m' + data.cols + '">\
        <select id="' + fieldId + '" name="' + name + '"></select>\
        <label for="' + fieldId + '" class="">' + data.label + '</label>\
    </div>\
    ';
};