"use strict";

var _ = require('underscore');
var path = require('path');
var helper = require(path.join(LIBS_DIR, 'helper'));

module.exports = function(name, data) {
    var fieldId = name.replace('[', '_');
    fieldId = fieldId.replace(']', '_');
    var str = '\
    <div class="input-field nb-string-array col s12 m' + data.cols + '">\
        <div class="nb-string-array-header">' + data.label + '</div>\
        <ul class="nb-string-array-content">';
    _.map(data.list, function(l) {
        str += '\
            <li>\
                <div class="info"><span>' + l.user.firstname + ' ' + l.user.lastname + ':</span> ' + helper.isoDateToNbDateString(l.time) + '</div>\
                <div class="content">' + l.content + '</div>\
                <div class="delete-item">x</div>\
            </li>';
    });
    str += '</ul>\
    </div>';
    return str;
};