"use strict";

module.exports = function(name, data) {
    var fieldId = name.replace('[', '_');
    fieldId = fieldId.replace(']', '_');
    var checked = (data.value == 'on') ? ' checked' : '';
    return '\
    <div class="nb-switch-field col s12 m' + data.cols + '">\
        <label class="nb-switch-label" for="' + fieldId + '">' + data.label + '</label>\
        <div class="switch clearfix">\
            <label for="' + fieldId + '">\
                ' + lang('Off') + '\
                <input name="' + name + '" id="' + fieldId + '" type="checkbox"' + checked + ' />\
                <span class="lever"></span>\
                ' + lang('On') + '\
            </label for="' + fieldId + '">\
        </div>\
    </div>';
};