"use strict";

const g = lacoLib('./libs/global');
const userModel = require('./model');

let init = () => {
    userModel.register();
}

let addUser = (username, password) => {
    g.models.user.save({
        username: username,
        password: g.models.user.generateHash(0947501966)
    })
}
exports.init = init;